import { HttpOptions } from '@tmagic/schema';
import { HttpDataSourceOptions, HttpDataSourceSchema } from '../types';
import DataSource from './Base';
/**
 * Http 数据源
 * @description 通过 http 请求获取数据
 */
export default class HttpDataSource extends DataSource {
    type: string;
    isLoading: boolean;
    error?: {
        msg?: string;
        code?: string | number;
    };
    schema: HttpDataSourceSchema;
    httpOptions: HttpOptions;
    private fetch?;
    private beforeRequest;
    private afterRequest;
    constructor(options: HttpDataSourceOptions);
    init(): Promise<void>;
    request(options: HttpOptions): Promise<void>;
    get(options: Partial<HttpOptions> & {
        url: string;
    }): Promise<void>;
    post(options: Partial<HttpOptions> & {
        url: string;
    }): Promise<void>;
}
