/// <reference types="node" />
import EventEmitter from 'events';
import type { AppCore, CodeBlockContent, DataSchema } from '@tmagic/schema';
import type { DataSourceOptions } from '../types';
/**
 * 静态数据源
 */
export default class DataSource extends EventEmitter {
    type: string;
    id: string;
    isInit: boolean;
    data: Record<string, any>;
    app: AppCore;
    private fields;
    private methods;
    constructor(options: DataSourceOptions);
    setFields(fields: DataSchema[]): void;
    setMethods(methods: CodeBlockContent[]): void;
    getMethods(): CodeBlockContent[];
    setData(data: Record<string, any>): void;
    getDefaultData(): Record<string, any>;
    updateDefaultData(): void;
    init(): Promise<void>;
    destroy(): void;
}
